SELECT program.SITE_CODE,program.ORIGIN_ID,program.CHANNEL_ID,program.PART_ID,program.GLOBAL_ID,PLAY_NUMBER,PLAY_USER_NUMBER,COMMENT_NUMBER,COLLECTION_NUMBER,SHARE_NUMBER,program.TIME_LENGTH,PLAY_TIME,FROM_UNIXTIME(program.pub_date) AS pub_date, NAME AS siteName, ORIGIN_NAME AS originName, channel.CHANNEL_NAME AS channelName ,part.CHANNEL_NAME AS partName ,CHINESE_NAME AS programName FROM ((((log_day_xh_media_program_new_summary program INNER JOIN sys_groups groups ON (program.site_code=groups.id)) INNER JOIN index_xh_media_origin origin ON (program.site_code=origin.site_code AND program.origin_id=origin.origin_id)) INNER JOIN index_xh_media_channel channel ON (program.site_code=channel.site_code AND program.origin_id=channel.origin_id AND program.CHANNEL_ID=channel.CHANNEL_ID AND channel.PARENT_ID=-1)) INNER JOIN index_xh_media_channel part ON (program.site_code=part.site_code AND program.origin_id=part.origin_id AND program.CHANNEL_ID=part.PARENT_ID)) INNER JOIN index_xh_media_program indexprogram ON (program.site_code=indexprogram.site_code AND program.CHANNEL_ID=indexprogram.CHANNEL_ID AND program.GLOBAL_ID=indexprogram.GLOBAL_ID) WHERE
program.SITE_CODE = #{siteCode,jdbcType=VARCHAR}
AND program.ORIGIN_ID = #{originId,jdbcType=VARCHAR}
AND program.CHANNEL_ID = #{channelId,jdbcType=VARCHAR}
AND program.PART_ID = #{partId,jdbcType=VARCHAR}
AND program.GLOBAL_ID = #{globalId,jdbcType=VARCHAR}

SELECT date,site_code,origin_id,channel_id,part_id,global_id,SUM(play_number) AS play_number, SUM(play_user_number) AS play_user_number, SUM(comment_number) AS comment_number,SUM(collection_number) AS collection_number,SUM(share_number) AS share_number, SUM(play_time) AS play_time, FROM_UNIXTIME(pub_date) AS pub_date,time_length 
FROM log_day_xh_media_program_new_summary 
WHERE site_code in 

          #{item}       

AND (date BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR})

    AND site_code = #{siteCode,jdbcType=VARCHAR} 


    AND origin_id = #{originId,jdbcType=VARCHAR} 


    AND channel_id = #{channelId,jdbcType=VARCHAR} 


    AND part_id = #{partId,jdbcType=VARCHAR}


    AND global_id = #{globalId,jdbcType=VARCHAR} 

GROUP BY site_code,origin_id,channel_id,part_id,global_id ORDER BY 

          ${item}

SELECT date,site_code,origin_id,channel_id,part_id,global_id,SUM(play_number) AS play_number, SUM(play_user_number) AS play_user_number, SUM(comment_number) AS comment_number,SUM(collection_number) AS collection_number,SUM(share_number) AS share_number, SUM(play_time) AS play_time, FROM_UNIXTIME(pub_date) AS pub_date,time_length 
FROM log_day_xh_media_program_new_summary 
WHERE site_code = #{siteCode,jdbcType=VARCHAR} AND global_id = #{globalId,jdbcType=VARCHAR} 
GROUP BY date,site_code,origin_id,channel_id,part_id,global_id ORDER BY 

          ${item}
